import { Component } from '@angular/core';

@Component({
  selector: 'app-registrarse',
  templateUrl: './registrarse.component.html',
  styleUrls: ['./registrarse.component.sass']
})
export class RegistrarseComponent {

}

// Importa jQuery para TypeScript
import * as $ from 'jquery';

// Extiende las reglas de validación de jQuery Validator para el RUT chileno
$.validator.addMethod(
    "validarRut",
    function (value: string, element: any) {
        return this.optional(element) || validarRut(value);
    },
    "Ingresa un RUT válido"
);

